import React from 'react';
import StudentsService from '../services/StudentsService'
import StudentForm from '../components/StudentForm'
import Modal from 'react-modal';



const  studentsService  =  new  StudentsService();
const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};



class StudentListCompoment extends React.Component {
handleDelete(e,pk){
   e.preventDefault()
    var  self  =  this;
    studentsService.deleteStudent({id :  pk}).then(()=>{
        var  newArr  =  self.state.students.filter(function(obj) {
            return  obj.id  !==  pk;
        });
        self.setState({
        students:  newArr})
    });
};
constructor(){
super()
 this.state = {
     addStudentForm: false,
     modalIsOpen: false

  };
   this.openModal = this.openModal.bind(this);
}
  openModal() {
    this.setState({modalIsOpen: true});
  }


  closeModal() {
    this.setState({modalIsOpen: false});
  }


render(){

return(


<div>
{this.state.addStudentForm &&  <StudentForm />}
 <button onClick={this.openModal} type="button" class="btn btn-secondary">Add student</button>
 <Modal
          isOpen={this.state.modalIsOpen}
          onAfterOpen={this.afterOpenModal}
          onRequestClose={this.closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >

          <div>I am a modal</div>
          <StudentForm/>
           <button onClick={this.closeModal}>Close</button>
                      <button onClick={this.closeModal}>Submit</button>
 </Modal>

<table class="table">
  <thead class="thead-dark">
    <tr>
      <th scope="col">#</th>
      <th scope="col">Username</th>
      <th scope="col">Group</th>
      <th scope="col">Actions</th>

    </tr>
  </thead>
  <tbody>
   {this.props.data.map((student, index)=>
    <tr>
      <th scope="row">{student.id}</th>
      <td>{student.username}</td>
      <td>{student.group}</td>
      <td><button type="button" class="btn btn-secondary">Edit</button>
      <button onClick={(e)=>  this.handleDelete(e,student.id) } type="button" class="btn btn-secondary">Delete</button>

</td>
    </tr>
    )}

  </tbody>
</table>

</div>

)}}
export default StudentListCompoment;
