import React from 'react';
import StudentListCompoment from '../components/StudentList'
import axios from 'axios'

import StudentsService from '../services/StudentsService'

const  studentsService  =  new  StudentsService();


class StudentForm extends React.Component{
constructor(props){
super(props);
this.state={
groups:[]}
this.handleSubmit = this.handleSubmit.bind(this)}

componentDidMount(){
 var  self  =  this;
 studentsService.getGroups().then(result=> {
        self.setState({ groups:  result})
    });
 const { match: { params } } =  this.props;
    if(params  &&  params.pk)
    {
        studentsService.getStudent(params.pk).then((c)=>{
            this.refs.username.value  =  c.username;
            this.refs.group.value  =  c.group;

        })
    };

        };

handleSubmit(event) {
    const { match: { params } } =  this.props;
    if(params  &&  params.pk){
        this.handleUpdate(params.pk);
    }
    else
    {
        this.handleCreate();
    }
    event.preventDefault();
}
handleCreate(){
    studentsService.createStudent(
        {
        "username":  this.refs.username.value,
        "group":  this.refs.group.value,

        }).then((result)=>{
                alert("Customer created!");
        }).catch(()=>{
                alert('There was an error! Please re-check your form.');
        });
}

handleUpdate(pk){
studentsService.updateStudent(
    {
   "id":pk,
   "username":  this.refs.username.value,
        "group":  this.refs.group.value
    }
    ).then((result)=>{

        alert("Customer updated!");
    }).catch(()=>{
        alert('There was an error! Please re-check your form.');
    });
}
render(){
return (
<div><div class="modal" tabindex="-1" role="dialog">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Edit student</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
  <form onSubmit={this.handleSubmit}>
  <div class="form-group">
    <label for="username">Student Name</label>
    <input type="text" class="form-control" id="username" placeholder="Your full Name"/>
  </div>
  <div class="form-group">
    <label for="groups">Select Group</label>
    <select class="form-control" id="groups">
    {this.props.data.map((group, index)=>
      <option>group.name</option>
     )}
    </select>
  </div>
  </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div></div>



)}
}
export default StudentForm;
