import React from 'react';
import StudentsService from '../services/StudentsService'
import StudentForm from '../components/StudentForm'
import Modal from 'react-modal';

Modal.setAppElement('body');


const  studentsService  =  new  StudentsService();
const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};



class StudentListCompoment extends React.Component {
handleDelete(e,pk){
    var  self  =  this;
    studentsService.deleteStudent({id :  pk}).then((result)=>{
        const students_list = this.state.students.filter((post) => {
                    return pk !== post.id;});
        this.setState(state => {
                    state.students = this.srudents_list;
                    return state;})

                alert("Customer deleted!");
        }).catch(()=>{
                alert('There was an error! Please re-check your form.');
        });
};

constructor(){
super()
 this.state = {
     addStudentForm: false,
     modalIsOpen: false,
     requestType:''

  };
   this.openModal = this.openModal.bind(this);
   this.closeModal = this.closeModal.bind(this);

}
  openModal(e, type, studentId) {
    const studID = studentId ? studentId:null
    this.setState({modalIsOpen: true, requestType:type});
  }


  closeModal() {
    this.setState({modalIsOpen: false});
  }


render(){
console.log(this.props.data)
return(


<div>
 <button onClick={(e)=>this.openModal(e,'post')} type="button" class="btn btn-secondary">Add student</button>
 <Modal

          isOpen={this.state.modalIsOpen}
          onRequestClose={this.closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >

          <div>I am a modal</div>
          <StudentForm requestType={this.state.requestType} studentID={this.studID}/>
           <button onClick={this.closeModal}>Close</button>
 </Modal>

<table class="table">
  <thead class="thead-dark">
    <tr>
      <th scope="col">#</th>
      <th scope="col">Username</th>
      <th scope="col">Group</th>
      <th scope="col">Actions</th>

    </tr>
  </thead>
  <tbody>
   {this.props.data.map((student, index)=>
    <tr>
      <th scope="row">{student.id}</th>
      <td>{student.username}</td>
      <td>{student.group}</td>
      <td><button onClick={(e)=> this.openModal(e,'put',student.id)} type="button" class="btn btn-secondary">Edit</button>
      <button onClick={(e)=>  this.handleDelete(e,student.id) } type="button" class="btn btn-secondary">Delete</button>

</td>
    </tr>
    )}

  </tbody>
</table>

</div>

)}}
export default StudentListCompoment;
